import React from 'react';
class Controlledforms extends React.Component{
    constructor(){
       super();
       this.state={
           firstname: '',
           lastname: '',
           email :'',
           gender : '',
           courses :'react' ,
           errors : {
            firstname: '',
            lastname: '',
            email :'',
            gender : '',
            
           }
       };
    }
    handlesubmit=(e)=>{
      e.preventDefault();
    //   var errkeys= Object.keys(this.state).filter((key)=>{
    //       if(this.state[key]==='' && key!='errors' ){
    //           return key;
    //       }
    //   });
    //   if(errkeys.length>=1){
    //       console.error("please fill the required fields")
    //   }
    //   else{
    //       console.log(this.state)
    //   }
    };
    handlechange=(e)=>{
        var errors={...this.state.errors}
        if(e.target.value==''){
            errors[e.target.name]=`${e.target.name}is required`;
        }
        else{
            errors[e.target.name] = '';
        }
        this.setState({ errors, [e.target.name] :e.target.value})
    };
    render(){
        return(
            <form onSubmit={(e)=>this.handlesubmit(e)}>
                <h2 >user forms</h2>
                <label>firstname:</label>
                <input type="text" name="firstname" value={this.state.firstname}
                onChange ={(e)=> this.handlechange(e)}/><br/><br/>
                <span style={{color:'red'}}>{this.state.errors.firstname}</span><br></br>
                <label>lastname:</label>
                <input type="text" name="lastname" value={this.state.lastname}
                 onChange ={(e)=> this.handlechange(e)}/><br/><br/>
                <label>email:</label>
                <input type="email" name="email" value={this.state.email}
                 onChange ={(e)=> this.handlechange(e)}/><br/><br/>
                <label>gender:</label>&nbsp;&nbsp;
                <label>Male:</label>
                 <input type="radio" name="gender" value="male"
                 onChange ={(e)=> this.handlechange(e)}/>
                 <label>Female:</label>
                 <input type="radio" name="gender" value="female"
                  onChange ={(e)=> this.handlechange(e)}/><br/><br/>
                  <label>courses</label>
                 <select name="courses" value={this.state.courses}
                 onChange={(e)=>this.handlechange(e)}>
                     <option>react</option>
                     <option>mongodb</option>
                     <option>nodejs</option>
                     <option>javascript</option>
                 </select><br/><br/>
                 <button type="submit">submit</button>&nbsp;&nbsp;
                 <button type="submit">Reset</button>
            </form>
        )
    }
}
export default Controlledforms
