import React from 'react';
import axios from 'axios';
class Crud extends React.Component{
constructor(){
    super();
    this.state={
        user:[],
        id:'' ,
        name: '' ,
        age : '' ,
        email : '' ,
      }
    };
async componentDidMount(){
    var response=await axios.get('https://623bfad68e9af5878949efc3.mockapi.io/users');
    this.setState({user:response.data})
}
handleReset=()=>{
  this.setState({
      name:'',
      age: '',
      email:'' 
  })
}
handleSubmit=()=>{
    
}
handleUpdate=()=>{


}
handleCreate=async()=>{
var response= await axios.post('https://623bfad68e9af5878949efc3.mockapi.io/users',
{
    name : this.state.name,
    age : this.state.age,
    email : this.state.email
})
var user=[...this.state.user]
user.push(response.data)
this.setState({user,name:'',age: '',email:''})


}
onPopulate=(id)=>{
    var selectedata= this.state.user.filter((user)=>user.id===id)[0];
   this.setState({
       id: selectedata.id,
       name:selectedata.name,
       age :selectedata.age,
       email: selectedata.email
   });
    
}
render(){
    return(
        <>
        <h2>crud component</h2>
        <h2>user forms</h2>
        <form  onSubmit={this.handleSubmit}>
            <label>Name: </label>
            <input type="text" name="name" value={this.state.name} onChange={(e)=>this.setState({name:e.target.value})}></input><br/><br/>
            <label>Age: </label>
            <input type="number" name="age" value={this.state.age} onChange={(e)=>this.setState({age:e.target.value})}></input><br/><br/>
            <label>Email:  </label>
            <input type="email" name="email" value={this.state.email} onChange={(e)=>this.setState({email:e.target.value})}></input><br/><br/>
            <button type="submit">submit</button>&nbsp;&nbsp;
            <button type="submit" onClick={this.handleReset}>reset</button>
        </form>
        <h2>user details</h2>
        <table border={1}>
        <tr> 
            <th>id</th>
            <th>Name</th>
            <th>age</th>
            <th>email</th>
            <th>Action</th>
        </tr>
        <tbody>
            {this.state.user.map((data)=>(     
              <tr key={data.id}>
               <td>{data.id}</td>
               <td>{data.name}</td>
               <td>{data.age}</td>
               <td>{data.email}</td>
               <td>
                   <button onClick={()=>this.onPopulate(data.id)}>update</button>
                   <button onClick={this.handleDelete}>delete</button>
               </td>
              </tr>


            ))}
        </tbody>
       


        </table>
        
        </>
    )
}
}
export default Crud;
