import React from 'react';
class Controlledforms extends React.Component{
    constructor(){
       super();
       this.state={
           firstname: '',
           lastname: '',
           email :'',
           gender : '',
           courses :''
       }
    }
    render(){
        return(
            <form >
                <h2 >user forms</h2>
                <label>firstname:</label>
                <input type="text" name="firstname" value={this.state.firstname}
                onChange ={(e)=> this.setState({firstname :e.target.value})}/><br/><br/>
                <label>lastname:</label>
                <input type="text" name="lastname" value={this.state.lastname}
                onChange ={(e)=> this.setState({lastname : e.target.value})}/><br/><br/>
                <label>email:</label>
                <input type="email" name="email" value={this.state.email}
                onChange ={(e)=>this.setState({email: e.target.value})}/><br/><br/>
                <label>gender:</label>&nbsp;&nbsp;
                <label>Male:</label>
                 <input type="radio" name="gender" value="male"
                 onChange ={(e)=>this.setState({"gender":e.target.value})}/>
                 <label>Female:</label>
                 <input type="radio" name="gender" value="female"
                 onChange ={(e)=>this.setState({"gender":e.target.value})}/><br/><br/>
                 <select>
                     <option>react</option>
                     <option>mongodb</option>
                     <option>nodejs</option>
                     <option>javascript</option>
                 </select><br/><br/>
                 <button type="submit">submit</button>&nbsp;&nbsp;
                 <button type="submit">Reset</button>
            </form>
        )
    }
}
export default Controlledforms
