import { useState } from 'react';
import './App.css'
function App(){
    const names=["mahi","reji","arju"];
    const [like,setLike] = useState(0)
    return(
        <div>
        {names.map((nm)=>(<Message name={nm}/>))}
        <button onClick={()=>setLike(like+1)}>üëç{like}</button>
        
        </div>
    );

} export default App;
function Message(props){
    return(
        <div>
            <h2>hello{props.name} </h2>
        </div>
    );
}
